const fs = require('fs');
// get prism.css
const css = fs.readFileSync('./src/css/prism.css', 'utf-8');
// write theme-css.js
fs.writeFile(
  './src/components/highlight/theme-css.js',
  `// do not edit this file
// it is generated by src/css/prism.css

const highlightThemeCss = \`${css.replace(/\s+/g, '')}\`;

export { highlightThemeCss };
`,
  err => {
    if (err) return console.log(err);
    console.log('📝 Wrote theme-css.js');
  }
);
